mapper.select:
SELECT * FROM user
where 1=1
 and login_name=#{loginName}
and password=#{password}

mapper.insert:
INSERT INTO user (login_name, password, user_name, phone, authority, login_ip) 
VALUES 
(#{loginName},#{password},#{userName},#{phone},#{authority},#{loginIp});

mapper.update:
update user
login_name=#{loginName}, password=#{password}, user_name=#{userName}, phone=#{phone}, authority=#{authority}, login_ip=#{loginIp},
where id=#{id}
